mapper.insert:
INSERT INTO REVIEW(    COURSE_NUMBER
						   ,UNIVERSITY
						  , SEMESTER
					      , STAR_POINT
					      , COURSE_REVIEW
					      ,PROFESSOR
					      ,COURSE_NAME

					      )
              VALUES(    
              		   #{course_number}
              		  , #{university}
              		  , #{semester}
              		  , #{star_point}
              		  , #{course_review}
              		  , #{professor}
              		  , #{course_name}
        	           )

mapper.select:
SELECT COUNT(*)
FROM REVIEW
WHERE COURSE_NAME=#{course_name} AND STUDENT_ID=#{student_id}

SELECT COUNT(*)
FROM REGISTRATION
WHERE COURSE_NAME=#{course_name} AND STUDENT_ID=#{student_id}

SELECT COURSE_NUMBER
FROM REGISTRATION
WHERE COURSE_NAME=#{course_name}

SELECT AVG(STAR_POINT)
FROM REVIEW
WHERE COURSE_NAME=#{course_name} AND PROFESSOR=#{professor}

SELECT COURSE_NAME, COURSE_NUMBER, CREDIT, UNIVERSITY, PROFESSOR, MAJOR FROM ( SELECT COURSE_NAME, COURSE_NUMBER, CREDIT, UNIVERSITY, PROFESSOR, MAJOR, ROW_NUMBER() OVER(ORDER BY COURSE_NUMBER ASC) AS RNUM FROM COURSE WHERE 1=1
AND COURSE_NAME LIKE CONCAT('%',#{keyword},'%') AND COURSE_NUMBER LIKE CONCAT('%',#{keyword},'%') AND PROFESSOR LIKE CONCAT('%',#{keyword},'%') AND MAJOR LIKE CONCAT('%',#{keyword},'%') AND UNIVERSITY LIKE CONCAT('%',#{keyword},'%') AND CREDIT LIKE CONCAT('%',#{keyword},'%') AND COURSE_NAME LIKE CONCAT('%',#{keyword},'%') OR COURSE_NUMBER LIKE CONCAT('%',#{keyword},'%') OR PROFESSOR LIKE CONCAT('%',#{keyword},'%') OR MAJOR LIKE CONCAT('%',#{keyword},'%') OR UNIVERSITY LIKE CONCAT('%',#{keyword},'%') OR CREDIT LIKE CONCAT('%',#{keyword},'%')
GROUP BY COURSE_NAME,PROFESSOR,MAJOR ) MP WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}
